mapper.select:
SELECT id 
FROM users
WHERE id=#{id}

SELECT id, pwd, email, profile, regdate
FROM users
WHERE id=#{id}

mapper.update:
UPDATE users
SET pwd=#{newPwd }
WHERE id=#{id }

UPDATE users
SET profile=#{profile }
WHERE id=#{id }

UPDATE users
SET email=#{email }, profile=#{profile }
WHERE id=#{id }

mapper.select:
SELECT id, name
FROM users 
WHERE name= #{name} AND email= #{email}

mapper.update:
UPDATE users
SET email=#{email } 
WHERE id=#{id }
